import NewMenu from "@/components/NewMenu.vue";
export default {
  name: 'newsdetail',
  data: {
    id: '',
    Bodydata: {}
  },
  components: {
    NewMenu: NewMenu
  },
  mounted: function mounted() {
    var _this = this;

    this.id = this.$route.params.id;
    axios.get('api/4/news/' + this.id).then(function (response) {
      // response.data.body = this.attachBodyContent(response.data.body);
      _this.Bodydata = response.data;
      setTimeout(function () {
        _this.updatelastestContent({
          'image': _this.Bodydata.image,
          'title': _this.Bodydata.title,
          'id': _this.Bodydata.id
        });
      }, 1);
    }).catch(function (error) {
      console.log(_this.error);
    });
  }
};
