import Vuex from 'vuex'
import Vue from 'vue'

Vue.use(Vuex)

const mainOption = {
    namespace: true,
    state: {
        lastestContent: '',
        id: '',
        longCommentN: '',
        shortCommentN: '',
        share: true,
    },
    actions: {

    },
    mutations: {
        updateId(state, value) {
            state.id = value;
        },
        updateLastestContent(state, value) {
            state.lastestContent = value;
        },
        updateLongCommentN(state, value) {
            state.longCommentN = value;
        },
        updateShortCommentN(state, value) {
            state.shortCommentN = value;
        },
        updateShare(state, value) {
            state.share = value;
        },
    },
    getters: {

    }
}

export default new Vuex.Store({
    modules: {
        mainOption
    }
})
